import React from "react";
import myImage from "../img/portfolio.jpg";
import { FaJava, FaHtml5, FaJs, FaReact, FaPython, FaGithub, FaAws, FaNodeJs, FaInstagram, FaLinkedin } from 'react-icons/fa'

class About extends React.Component {
  constructor() {
    super();
    this.state = {
      skills: [
        { id: "java", content: "Java", icon: <FaJava /> },
        { id: "html_css", content: "HTML/CSS", icon: <FaHtml5 /> },
        { id: "javascript", content: "JavaScript", icon: <FaJs /> },
        { id: "react", content: "React.js", icon: <FaReact /> },
        { id: "python", content: "Python", icon: <FaPython /> },
        { id: "git", content: "Git", icon: <FaGithub /> },
        { id: "aws", content: "AWS", icon: <FaAws /> },
        { id: "node", content: "Node.js", icon: <FaNodeJs /> }
      ],
      about_me: [
        {
          id: "about-paragrah",
          content:
            "Hello! I'm a Computer Science and Economics double major at Vanderbilt University with a passion for full-stack development and exploring the vast potential of cloud computing. While my current focus involves developing full-stack research applications, I'm also intrigued by learning how technological innovation and economic principles intersect and influence each other. I'm eager to apply my interdisciplinary knowledge to create innovative solutions and continue growing my expertise.",
        },
        {
          id: "location",
          content: (
            <div
              style={{
                display: "flex",
                alignItems: "center",
                color: "#FF6347", // Tomato red
                fontSize: "16px",
                fontWeight: "bold",
              }}
            >
              <i
                className="ion-ios-location"
                style={{
                  fontSize: "20px",
                  marginRight: "8px",
                  verticalAlign: "middle",
                }}
              ></i>
              Nashville, TN
            </div>
          ),
        },
        {
          id: "resume-link",
          content: (
            <div>
              <a
                href="https://drive.google.com/file/d/15u7r4-TTCLbQ-uajltGlXEHwFKeqxj7J/view?usp=drive_link"
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  display: "inline-block",
                  padding: "10px 20px",
                  fontSize: "16px",
                  fontWeight: "bold",
                  color: "#fff",
                  backgroundColor: "#0078ff",
                  borderRadius: "8px",
                  textDecoration: "none",
                  boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                  transition: "background-color 0.3s, transform 0.2s",
                }}
                onMouseEnter={(e) =>
                  (e.target.style.backgroundColor = "#0056b3")
                }
                onMouseLeave={(e) =>
                  (e.target.style.backgroundColor = "#0078ff")
                }
              >
                <i
                  className="ion-ios-cloud-download"
                  style={{
                    marginRight: "8px",
                    fontSize: "18px",
                    verticalAlign: "middle",
                  }}
                ></i>
                Resume
              </a>
            </div>
          ),
        }
      ],
    };
  }

  render() {
    return (
      <section id="about" className="about-mf sect-pt4 route">
        <div className="container">
          <div className="row">
            <div className="col-sm-12">
              <div className="box-shadow-full">
                {/* Title at the top */}
                <div className="title-box-2">
                  <h5 className="title-left">About Me</h5>
                </div>

                <div className="row">
                  {/* Image on the left */}
                  <div className="col-md-4">
                    <div className="about-img" style={{ textAlign: "center" }}>
                      <img
                        className="img-fluid rounded b-shadow-a"
                        src={myImage}
                        alt="Profile"
                      />
                    </div>
                  </div>

                  {/* About Me text on the right */}
                  <div className="col-md-8">
                    <div className="about-me">
                      {this.state.about_me.map((content) => {
                        return (
                          <p className="lead" key={content.id}>
                            {content.content}
                          </p>
                        );
                      })}

                      {/* Social Icons */}
                      <div className="socials" style={{ paddingTop: "0" }}>
  <ul>
    <li>
      <a href="https://www.instagram.com/hua.jin_" target="_blank" rel="noopener noreferrer">
        <span className="ico-circle">
          <FaInstagram />
        </span>
      </a>
    </li>
    <li>
      <a href="https://github.com/HuaJin16" target="_blank" rel="noopener noreferrer">
        <span className="ico-circle">
          <FaGithub />
        </span>
      </a>
    </li>
    <li>
      <a href="https://www.linkedin.com/in/huajin16/" target="_blank" rel="noopener noreferrer">
        <span className="ico-circle">
          <FaLinkedin />
        </span>
      </a>
    </li>
  </ul>
</div>
                    </div>
                  </div>
                </div>

                {/* Tech Stack section */}
                <div className="row mt-5">
                  <div className="col-md-12">
                    <div className="skill-mf">
                      <p className="title-s">Tech Stack</p>
                      <div
                        style={{
                          display: "flex",
                          flexWrap: "wrap",
                          gap: "20px",
                          justifyContent: "flex-start",
                        }}
                      >
{this.state.skills.map((skill) => (
  <div
    key={skill.id}
    style={{
      textAlign: "center",
      padding: "15px",
      borderRadius: "8px",
      backgroundColor: "#f8f9fa",
      minWidth: "120px",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    }}
  >
    {skill.isReactIcon ? (
      <span style={{
        fontSize: "24px",
        color: "#0078ff",
        marginBottom: "8px",
        display: "block",
      }}>
        {skill.icon}
      </span>
    ) : (
      <i
        className={skill.icon}
        style={{
          fontSize: "24px",
          color: "#0078ff",
          marginBottom: "8px",
          display: "block",
        }}
      />
    )}
    <p
      style={{
        margin: "0",
        fontWeight: "500",
        fontSize: "14px",
      }}
    >
      {skill.content}
    </p>
  </div>
))} 
                      </div>
                    </div>
                  </div>
                </div>

              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
